package hello;

public class pyramideExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=5;i++) {//outer loop
	for(int j=1;j<=i;j++){//inner loop
		System.out.print(" * ");
		
		
	}
	System.out.println();//new line
}

 for(int i=1;i<=5;i++) {
	 for(int j=5;j>=i;j--) {
		 System.out.print(" * ");
	 }
	 System.out.println();
 }
		
		
	}

}
                               